/* change status of meeting */
function customer_visited(partner_id){
    button = partner_id + "_meeting";
    openerp.jsonRpc("/crm/meeting/visited", 'call', {
        'partner_id': partner_id,
    }).done(function(data){
        if(data == "meeting_done"){
            $("#" + button).addClass("hidden");
        }
    });
}

